        private void ConfigureLabel(System.Windows.Forms.Label lbl, string text, int x, int y, bool bold)
        {
            lbl.AutoSize = true;
            lbl.Location = new System.Drawing.Point(x, y);
            lbl.Text = text;
            if (bold)
            {
                lbl.Font = new System.Drawing.Font("Microsoft Sans Serif", 8.25F, System.Drawing.FontStyle.Bold);
            }
        }

        private void ConfigureClickableLabel(System.Windows.Forms.Label lbl, string text, int x, int y, System.EventHandler handler, int width = 60)
        {
            ConfigureLabel(lbl, text, x, y, false);
            lbl.Cursor = System.Windows.Forms.Cursors.Hand;
            lbl.Click += handler;
            // Set minimum size so empty labels are still clickable
            lbl.AutoSize = false;
            lbl.Size = new System.Drawing.Size(width, 20);
        }

        private void ConfigureTrackBar(System.Windows.Forms.TrackBar tb, int x, int y, System.EventHandler handler)
        {
            tb.Location = new System.Drawing.Point(x, y);
            tb.Size = new System.Drawing.Size(65, 20);
            tb.Maximum = 100;
            tb.Value = 100;
            tb.TickStyle = System.Windows.Forms.TickStyle.None;
            tb.ValueChanged += handler;
        }

        private void ConfigureButton(System.Windows.Forms.Button btn, string text, int x, int y, System.EventHandler handler)
        {
            btn.Location = new System.Drawing.Point(x, y);
            btn.Size = new System.Drawing.Size(30, 23);
            btn.Text = text;
            btn.UseVisualStyleBackColor = true;
            btn.Click += handler;
        }

        private void ConfigureSRCheckbox(System.Windows.Forms.CheckBox chk, int x, int y, int rx, int sr)
        {
            chk.AutoSize = true;
            chk.Location = new System.Drawing.Point(x, y);
            chk.Size = new System.Drawing.Size(15, 14);
            chk.Tag = rx.ToString() + "," + sr.ToString();
            chk.CheckedChanged += new System.EventHandler(this.SRCheckbox_CheckedChanged);
        }

